import {
  app, protocol, BrowserWindow, Menu, Tray, nativeImage,
} from 'electron';
import {
  createProtocol,
  installVueDevtools,
} from 'vue-cli-plugin-electron-builder/lib';
import log from 'electron-log';
import { autoUpdater } from 'electron-updater';


log.info('---------------- STARTING PROGRAMM ----------------');

const iconPath = 'data:image/png;base64,';
let systrayMenu = null;
let forceQuit = false;
let appIcon = null;
const isDevelopment = process.env.NODE_ENV !== 'production';

// Keep a global reference of the window object, if you don't, the window will
// be closed automatically when the JavaScript object is garbage collected.
let win;

// Standard scheme must be registered before the app is ready
protocol.registerStandardSchemes(['app'], { secure: true });

autoUpdater.on('update-downloaded', (info) => {
  autoUpdater.quitAndInstall();
});

function createWindow() {
  // Create the browser window.
  win = new BrowserWindow({ width: 800, height: 600 });
  if (isDevelopment || process.env.IS_TEST) {
    // Load the url of the dev server if in development mode
    win.loadURL(process.env.WEBPACK_DEV_SERVER_URL);
    if (!process.env.IS_TEST) win.webContents.openDevTools();
  } else {
    createProtocol('app');
    // Load the index.html when not in development
    win.loadURL('app://./index.html');
  }

  const template = [
    {
      label: app.getName(),
      submenu: [
        { role: 'quit' },
      ],
    },
    {
      label: 'Edit',
      submenu: [
        { role: 'copy' },
        { role: 'paste' },
      ],
    },
  ];
  Menu.setApplicationMenu(Menu.buildFromTemplate(template));

  // Systray Menu to restore window
  systrayMenu = Menu.buildFromTemplate([
    {
      label: 'Open',
      click: () => {
        win.show();
      },
    },
    {
      label: 'Quit',
      click: () => {
        forceQuit = true;
        app.quit();
      },
    },
  ]);

  appIcon = new Tray(nativeImage.createFromDataURL(iconPath)); // create systray
  appIcon.setToolTip('4myHealth-chelper'); // create systray tooltip
  appIcon.setContextMenu(systrayMenu);

  win.on('close', (event) => {
    if (!forceQuit) {
      event.preventDefault();
      win.hide();
      return false;
    }
    return true;
  });

  win.on('minimize', (event) => {
    forceQuit = false;
    event.preventDefault();
    win.hide();
  });
}


// Quit when all windows are closed.
/* app.on('window-all-closed', () => {
  // On macOS it is common for applications and their menu bar
  // to stay active until the user quits explicitly with Cmd + Q
  if (process.platform !== 'darwin') {
    app.quit();
  }
});
*/
app.on('activate', () => {
  // On macOS it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (win === null) {
    createWindow();
  }
});

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', async () => {
  if (isDevelopment && !process.env.IS_TEST) {
    // Install Vue Devtools
    await installVueDevtools();
  }
  autoUpdater.checkForUpdatesAndNotify();
  createWindow();
});

// Exit cleanly on request from parent process in development mode.
if (isDevelopment) {
  if (process.platform === 'win32') {
    process.on('message', (data) => {
      if (data === 'graceful-exit') {
        app.quit();
      }
    });
  } else {
    process.on('SIGTERM', () => {
      app.quit();
    });
  }
}
